mapper.update:
update book
set book_name   = #{name},
    book_author = #{author},
    book_price  = #{price}
where book_id = #{id};

mapper.select:
select book_id as id, book_name as name, book_author as author, book_price as price
from book
where book_id = #{value};

select book_id as id, book_name as name, book_author as author, book_price as price
from book
where book_name like concat('%', #{value}, '%');

select book_id as id, book_name as name, book_author as author, book_price as price
from book
where book_author = #{value};
